def customize_card(project_name,author_name,language="python"):
    string = '<div class="col-md-4"> <div class="card p-3 mb-2"> <div class="d-flex justify-content-between"> <div class="d-flex flex-row align-items-center"> <div class="icon"> <i class="bx bxl-mailchimp"></i> </div> <div class="ms-2 c-details"> <h6 class="mb-0">{author_name}</h6> <span>4 days ago</span> </div> </div> <div class="badge"> <span>{language}</span> </div> </div> <div class="mt-5"> <h3 class="heading">{project_name}</h3><br> <div class="badge"><button class="btn btn-primary btn-sm"> <i class="fa fa-plus"></i> Download </button> </div> <div class="mt-5"></div> </div> </div> </div> \n <!-- <p>vetha</p> -->'.format(project_name=project_name,author_name=author_name,language=language)
    return string